INSERT INTO `llista` (`id`, `nom`, `mail`, `conegut`, `created_at`, `updated_at`) VALUES
(1, 'TOIA CODINA D''OLOT', 'acodina2@xtec.cat', 'a través del correu.\r\nJORDI PAGÉS, que bé sentir notícies teves, felicitats pel vídeo, és molt interessant. El pròxim curs no sé on seré, he passat a ser suprimida. Tu continues fent de mestre de música? i de la Bet Nadal, què en sabem? Vinga, un petó ben grossssss\r\ntoia', '2012-06-20 11:02:49', '2012-06-20 11:02:49'),
(2, 'Mireia Cano Masllorens', 'mcano@joanpelegri.cat', 'Heu fet arribar a la meva escola un correu donant-vos a conèixer. Sóc mestra de música i voldria saber quins dels espectacles que teniu oferiu a les escoles,( m''ha semblat entendre que només oferiu el Migrasons), per a quina edat ho recomaneu, quants alumnes, quin espai necessiteu (teatre, aula gran...) i per quin preu. \r\nPenso que teniu propostes molt bones i voldria saber-ne més informació. \r\nPer cert, conec a l''Àlex Guitart!Vam estudiar turc junts.\r\n\r\nMoltes gràcies ', '2012-06-24 20:24:35', '2012-06-24 20:24:35'),
(3, 'Marc', 'mnogue65@xtec.cat', 'Hola, som una escola de primària i ens agradaria rebre els preus dels espectacles que feu.\r\n\r\nSalutacions,', '2012-06-28 13:35:18', '2012-06-28 13:35:18'),
(4, 'zsbntmm', 'dcyqav@nfwzyp.com', 'STGOHS  <a href="http://vgbwtwlcdcbx.com/">vgbwtwlcdcbx</a>, [url=http://kuxsedxfqmsp.com/]kuxsedxfqmsp[/url], [link=http://tfmbcxyjtdxt.com/]tfmbcxyjtdxt[/link], http://ybemjmguvpry.com/', '2012-08-09 17:21:47', '2012-08-09 17:21:47'),
(5, '', 'ratingsking@gmail.com', 'Statistics show that millions of people are now searching online for services or information about companies. Would you like to be the one they choose? We have been helping companies reach their full potential, rise above their competition and finally get an excellent return of investment. Why not give it a try? It', '2012-09-07 19:02:44', '2012-09-07 19:02:44'),
(6, 'ESCOLA LES ERES', 'e3005108@xtec.cat', 'heu enviat un mail a l''escola.\r\n\r\nm''interessa coneixer quines activitats oferiu per fer a l''escola des de P3 fins a 6è.\r\n\r\nGràcies.', '2012-09-10 10:31:08', '2012-09-10 10:31:08'),
(7, 'Rosana Garrido', 'rgarridopuche@gmail.com', ' Via mail, soc professora de secundaria als Salesians de St Boi, estic interessada en els concerts per les escoles.\r\nUrgent\r\nGràcies\r\nRosana', '2012-09-12 11:09:23', '2012-09-12 11:09:23'),
(8, 'Rosana Garrido', 'rgarridopuche@gmail.com', ' Via mail, soc professora de secundaria als Salesians de St Boi, estic interessada en els concerts per les escoles.\r\nUrgent\r\nGràcies\r\nRosana', '2012-09-12 11:09:28', '2012-09-12 11:09:28'),
(9, 'laura tello tresserras', 'lauratt73@gmail.com', 'Missatge rebut des de l''escola per activitats musicals adreçades al mestre/a de música.\r\n\r\nM''agradaria saber quin és el preu per l''especacle MIGRASONS.\r\ngràcies', '2012-09-12 22:37:16', '2012-09-12 22:37:16'),
(10, 'imma vargas segura', 'mvargas@xtec.cat', 'Us he conegut per un correu que heu enviat a l''escola.\r\nEm podrieu enviar preus o tarifes i també durada i logística que necessiteu dels espectacles i tallers que veniu a fer a l''escola.\r\nGràcies i salutacions', '2012-09-15 12:09:31', '2012-09-15 12:09:31'),
(11, 'laura tello tresserras', 'lauratt73@gmail.com', 'Bona tarda,\r\nA través de l''escola hem rebut la vostra oferta formativa pels nostres alumnes.\r\nEns podríeu dir els preus de les sessions i/o tallers?\r\nMoltes gràcies', '2012-09-18 15:52:55', '2012-09-18 15:52:55'),
(12, 'Jaume tihai', 'shubhamcata@yahoo.es', '...per casualitat.\r\n\r\nhey Jordi, esta molt guapo aixo, de moment si em podeu incloure per aqui posa-hi una foto de les que vas fer a l´estudi, i pots posar " Tabla hindu, bateria i percussions"  o quelcom semblant, si necesitas algo mes diguem-ho, \r\naveure si quedem per anar a la botiga aquella, quan et va be a tu?', '2012-09-28 17:24:59', '2012-09-28 17:24:59'),
(13, 'Felix R.', 'canari-grupoandino@hotmail.com', 'Hola somos un grupo en construcción de folklore sudamericano, y no se, si por casualidad conocéis algún músico que le gustara participar en este proyecto. \r\nGracias y un saludo.\r\nFelix-\r\ndireccion:  canari-grupoandino@hotmail.com', '2012-10-13 12:53:39', '2012-10-13 12:53:39'),
(14, 'merce ribera turró', 'merce.ribera@yahoo.es', 'bones, avui he rebut un missatge en el facebook ( merce ribera turro)perque havia demanat que fossim amics pero no he sabut contestar-vos ¿?  tinc el taller al carrer angel guimera de salt i m`agradaria molt coneixer-vos en directe.\r\nos deixo telefon 636609032\r\nmerceribera.plisweb.com  \r\n\r\nfins aviat, espero!!', '2013-06-04 16:25:55', '2013-06-04 16:25:55'),
(15, 'Neus Reginaldo Huix', 'arnauineus@yahoo.es', 'Pel correu de l''xtec. \r\nHola m''agradaria que m''enviessis preus de l''activitat de els vents del mon. Gràcies . Escola Miquel Carreras. Neus Reginaldo', '2013-06-23 09:02:41', '2013-06-23 09:02:41'),
(16, 'NOEL', 'neosugati@gmail.com', 'internet google', '2013-08-29 19:21:28', '2013-08-29 19:21:28'),
(17, 'Esther Cejudo', 'ecejudo@xtec.cat', 'HOla,\r\nM''ha arribat per correu la vostra tasca i m''ha semblat interessant. Sóc l''Esther especialista de música de la Zer del Cardener. M''agradaria saber quin pressupost em farieu per venir a l''escola el dia de Santa Cecília. Aquest és un dia que a l''escola ens visita algun músic.\r\n\r\nEspero contesta.\r\nAtentament,\r\n\r\nEsther', '2013-09-04 17:16:00', '2013-09-04 17:16:00'),
(18, 'Elisenda Planella Crespo', 'eplanella@casaldelsangels.org', 'Facebook', '2013-09-12 20:54:23', '2013-09-12 20:54:23');
